from enum import Enum


class ReturnCodeEnum(Enum):
    """Return Code Enum Class"""

    SUCCESS = (0, "Success")
    FAILED = (1, "Failed")
    PULP_IMPORT_ERR = (1005, "Pulp Module Import Failure")
    COIN_OR_CBC_ERR = (1006, "Coin-or-cbc Not Installed or Correctly Configured")

    @property
    def code(self):
        return self.value[0]

    @property
    def errmsg(self):
        return self.value[1]