"""
searcher
"""

_search_strategy_registry = {}


def strategy_register(name):
    def wrapper(func):
        _search_strategy_registry[name] = func
        return func

    return wrapper


def search(op_schedule_info):
    option = op_schedule_info.option
    strategy = option.get('strategy', 'hybrid')
    _search_strategy_registry.get(strategy)(op_schedule_info)
