"""
common apply action
"""
from auto_search.solution_space.action import apply_action_register
from auto_search.solution_space.action import PrepareActionType
from auto_search.compute_analysis import ComputePattern


@apply_action_register([ComputePattern.ELEMENTWISE, ComputePattern.BROADCAST, ComputePattern.POOLING,
                        ComputePattern.REDUCE, ComputePattern.TUPLE_REDUCE, ComputePattern.NORM,
                        ComputePattern.TRANSPOSE],
                       PrepareActionType.AT_ANALYSIS)
def apply(progress):
    """
    at analysis apply
    :param progress:
    :return:
    """
    progress.op_schedule_info.at_analysis()
