"""
reduce director
"""
from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import PrepareActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.prepare_action.at_analysis.at_analysis_action import AtAnalysisAction
from auto_search.solution_space.search_action.atomic.atomic_action import AtomicAction
from auto_search.compute_analysis import support_atomic_search


@director_register([ComputePattern.REDUCE, ComputePattern.TUPLE_REDUCE], PrepareActionType.FIRST_TODO)
def direct(progress):
    """
    reduce direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    if support_atomic_search(progress.op_schedule_info.output_tensors):
        next_action = AtomicAction(pattern)
    else:
        next_action = AtAnalysisAction(pattern)
    progress.todo = next_action
