from typing import List

from auto_search.utils import logger
from auto_search.solution_space.tensor_cfg import AxisInfo
from auto_search.solution_space.progress import Progress


def rfactor_in_at_target_stage_at(op_schedule_info, at_target_stage_index, at_targets, stage_index):
    """
    rfactor_in_at_target_stage_at
    :param op_schedule_info:
    :param at_target_stage_index:
    :param at_targets:
    :param stage_index:
    :return:
    """
    ub_split_last_reduce_axis = op_schedule_info.atomic_last_reduce_and_ub_split_last_reduce_axis
    ub_split_nlast_reduce_axis = op_schedule_info.atomic_last_reduce_and_ub_split_nlast_reduce_axis
    axis_info_list: List[List[AxisInfo]] = op_schedule_info.axis_info_list
    if ub_split_last_reduce_axis:
        at_target_stage_axis_info = axis_info_list[at_target_stage_index]
        for stage_axis_info in at_target_stage_axis_info:
            if stage_axis_info.type == "reduce_axis":
                at_targets[stage_index] = stage_axis_info
    elif ub_split_nlast_reduce_axis:
        at_target_stage_axis_info = axis_info_list[at_target_stage_index]
        for stage_axis_info in at_target_stage_axis_info:
            if stage_axis_info.type == "reduce_axis" and stage_axis_info.name.endswith('o'):
                at_targets[stage_index] = stage_axis_info


def rfactor_in_curr_stage_at(op_schedule_info, at_target_stage_index, at_targets, stage_index):
    """
    rfactor_in_curr_stage_at
    :param op_schedule_info:
    :param at_target_stage_index:
    :param at_targets:
    :param stage_index:
    :return:
    """
    axis_info_list: List[List[AxisInfo]] = op_schedule_info.axis_info_list
    at_target_stage_axis_info = axis_info_list[at_target_stage_index]
    for stage_axis_info in at_target_stage_axis_info:
        if stage_axis_info.type == "axis":
            at_targets[stage_index] = stage_axis_info


def reduce_atomic_rfactor_at(op_schedule_info, at_target_stage_index, at_targets, stage_index):
    """
    _reduce_atomic_rfactor_at
    :param op_schedule_info:
    :param at_target_stage_index:
    :param at_targets:
    :param stage_index:
    :return:
    """
    axis_info_list: List[List[AxisInfo]] = op_schedule_info.axis_info_list
    at_target_stage_axis_info = axis_info_list[at_target_stage_index]

    axis_type = "reduce_axis"
    if op_schedule_info.enable_atomic_ra_bind:
        axis_type = "fused_axis"

    for stage_axis_info in at_target_stage_axis_info:
        if stage_axis_info.type == axis_type:
            at_targets[stage_index] = stage_axis_info
            break


def proc(progress: Progress) -> bool:
    """
    last reduce并且ub切分last reduce场景，做了rfactor
    reduce_rf stage前面的stage at到reduce_rf stage的ub切分outer轴上;
    reduce_rf stage at到reduce stage上最后一个a轴上;
    reduce stage以及后面的stage at到res stage的ub切分之后的outer轴上;
    :param progress:
    :return:
    """
    op_schedule_info = progress.op_schedule_info
    if not op_schedule_info.is_atomic:
        return False

    if not (op_schedule_info.atomic_last_reduce_and_ub_split_last_reduce_axis ^
            op_schedule_info.atomic_last_reduce_and_ub_split_nlast_reduce_axis):
        return False

    stage_num = len(op_schedule_info.feature_tensor)
    at_targets = [None] * stage_num
    at_dict = op_schedule_info.at_dict

    for stage_index, stage in enumerate(op_schedule_info.schedule_obj.stages):
        at_target_stage_index = at_dict[stage_index]
        if at_target_stage_index is None:
            continue
        if str(stage.op).startswith("placeholder"):
            continue
        at_target_stage_type = op_schedule_info.stages_info[at_target_stage_index].get('type', [])
        curr_stage_type = op_schedule_info.stages_info[stage_index].get('type', [])
        if "reduce_rfactor" in at_target_stage_type:
            rfactor_in_at_target_stage_at(op_schedule_info, at_target_stage_index, at_targets, stage_index)
        elif "reduce_rfactor" in curr_stage_type:
            rfactor_in_curr_stage_at(op_schedule_info, at_target_stage_index, at_targets, stage_index)
        elif "reduce_atomic_rfactor" in curr_stage_type:
            reduce_atomic_rfactor_at(op_schedule_info, at_target_stage_index, at_targets, stage_index)

    op_schedule_info.at_targets = at_targets
    logger.debug('reduce_atomic_r02_ub_split_last_reduce_axis_at_rule')
    return True
