"""
common director
"""
from typing import NoReturn

from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.schedule_action.double_buffer.double_buffer_action \
    import DoubleBufferAction
from auto_search.solution_space.progress import Progress


@director_register([ComputePattern.ELEMENTWISE, ComputePattern.BROADCAST,
                    ComputePattern.REDUCE, ComputePattern.NORM, ComputePattern.POOLING, ComputePattern.TUPLE_REDUCE,
                    ComputePattern.TRANSPOSE], ScheduleActionType.AT)
def direct(progress: Progress) -> NoReturn:
    """
    common direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = DoubleBufferAction(pattern)
    progress.todo = next_todo
