"""
common director
"""
from typing import NoReturn

from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.progress import Progress


@director_register([ComputePattern.ELEMENTWISE, ComputePattern.BROADCAST, ComputePattern.REDUCE, ComputePattern.NORM,
                    ComputePattern.TRANSPOSE, ComputePattern.POOLING, ComputePattern.TUPLE_REDUCE],
                   ScheduleActionType.BUILD_CONFIG)
def direct(progress: Progress) -> NoReturn:
    """
    :param progress:
    :return:
    """
    next_todo = None
    progress.todo = next_todo
