"""
common director
"""
from typing import NoReturn

from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.prepare_action.update_progress.update_progress_action \
    import UpdateProgressAction
from auto_search.solution_space.schedule_action.build_config.build_config_action import BuildConfigAction
from auto_search.solution_space.progress import Progress


@director_register([ComputePattern.POOLING], ScheduleActionType.MEM_UNIQUE)
def direct(progress: Progress) -> NoReturn:
    """
    inline direct
    """
    pattern = progress.op_schedule_info.op_pattern
    next_action = UpdateProgressAction(pattern)
    progress.todo = next_action


@director_register([ComputePattern.TUPLE_REDUCE, ComputePattern.NORM], ScheduleActionType.MEM_UNIQUE)
def direct_01(progress: Progress) -> NoReturn:
    """
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = BuildConfigAction(pattern)
    progress.todo = next_todo
