"""
common director
"""
from typing import NoReturn

from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.schedule_action.set_store_predicate.set_store_predicate \
    import SetStorePredicateAction
from auto_search.solution_space.schedule_action.emit_insn.emit_insn_action \
    import EmitInsnAction
from auto_search.solution_space.progress import Progress


@director_register([ComputePattern.BROADCAST, ComputePattern.NORM],
                   ScheduleActionType.REUSE)
def direct(progress: Progress) -> NoReturn:
    """
    reuserd by direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = SetStorePredicateAction(pattern)
    progress.todo = next_todo


@director_register([ComputePattern.ELEMENTWISE], ScheduleActionType.REUSE)
def elementwise_direct(progress: Progress) -> NoReturn:
    """
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = EmitInsnAction(pattern)
    progress.todo = next_todo
