"""
rfactor action
"""
from enum import Enum
from enum import auto
from auto_search.solution_space.action import ConstAction
from auto_search.solution_space.action import ScheduleActionType


class RfactorType(Enum):
    """
    enum 5 rfactor scene
    """
    ATOMIC_RFACTOR = auto()
    SPLIT_LAST_REDUCE_AXIS_RFACTOR_FOR_ATOMIC = auto()
    SPLIT_NLAST_REDUCE_AXIS_RFACTOR_FOR_AROMIC = auto()
    SPLIT_LAST_REDUCE_AXIS_RFACTOR_FOR_NORMAL = auto()
    SPLIT_NLAST_REDUCE_AXIS_RFACTOR_FOR_NORMAL = auto()


class RfactorAction(ConstAction):
    """
    RfactorAction
    """
    def __init__(self, pattern, reduce_info_for_rfactor=None, rfactor_type=None):
        super().__init__(pattern, ScheduleActionType.RFACTOR)
        self.reduce_info_for_rfactor = reduce_info_for_rfactor
        self.rfactor_type = rfactor_type
