"""
common director
"""
from typing import NoReturn

from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.schedule_action.build_config.build_config_action \
    import BuildConfigAction
from auto_search.solution_space.schedule_action.pragma.pragma_action \
    import PragmaAction
from auto_search.solution_space.schedule_action.mem_unique.mem_unique_action \
    import MemUniqueAction
from auto_search.solution_space.progress import Progress


@director_register(
    [ComputePattern.ELEMENTWISE, ComputePattern.BROADCAST, ComputePattern.TRANSPOSE,
     ComputePattern.POOLING],
    ScheduleActionType.SET_BUFFER_SIZE)
def direct(progress: Progress) -> NoReturn:
    """
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = BuildConfigAction(pattern)
    progress.todo = next_todo


@director_register([ComputePattern.REDUCE], ScheduleActionType.SET_BUFFER_SIZE)
def reduce_direct(progress: Progress) -> NoReturn:
    """
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = PragmaAction(pattern)
    progress.todo = next_todo


@director_register([ComputePattern.TUPLE_REDUCE, ComputePattern.NORM], ScheduleActionType.SET_BUFFER_SIZE)
def direct_01(progress: Progress) -> NoReturn:
    """
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_todo = MemUniqueAction(pattern)
    progress.todo = next_todo
