"""
common director
"""
from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.schedule_action.rfactor.rfactor_action \
    import RfactorAction
from auto_search.solution_space.schedule_action.bind.bind_action \
    import BindAction
from auto_search.solution_space.schedule_action.reorder.reorder_action \
    import ReorderAction


@director_register([ComputePattern.ELEMENTWISE, ComputePattern.BROADCAST], ScheduleActionType.SPLIT)
def direct(progress):
    """
    comm split
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_action = BindAction(pattern)
    progress.todo = next_action


@director_register([ComputePattern.NORM, ComputePattern.POOLING], ScheduleActionType.SPLIT)
def norm_direct(progress):
    """
    norm direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_action = RfactorAction(pattern)
    progress.todo = next_action


@director_register([ComputePattern.TRANSPOSE], ScheduleActionType.SPLIT)
def transpose_direct(progress):
    """
    transpose direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_action = ReorderAction(pattern)
    progress.todo = next_action
