"""
reduce director
"""
from typing import NoReturn

from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import ScheduleActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.schedule_action.reorder.reorder_action \
    import ReorderAction
from auto_search.solution_space.schedule_action.rfactor.rfactor_action import RfactorAction
from auto_search.solution_space.schedule_action.rfactor.rfactor_action import RfactorType
from auto_search.solution_space.progress import Progress


@director_register([ComputePattern.TUPLE_REDUCE, ComputePattern.REDUCE], ScheduleActionType.SPLIT)
def direct(progress: Progress) -> NoReturn:
    """
    reduce direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    reduce_info_for_rfactor = progress.todo.do_rfactor_info
    if reduce_info_for_rfactor is None:
        progress.todo = ReorderAction(pattern)
        return
    # last reduce and ub split last reduce axis should do rfactor after split
    do_rfactor_after_split = reduce_info_for_rfactor.is_last_reduce \
                              and reduce_info_for_rfactor.ub_split_reduce_axis \
                              and reduce_info_for_rfactor.ub_split_last_reduce_axis
    if do_rfactor_after_split:
        if reduce_info_for_rfactor.is_atomic:
            progress.todo = RfactorAction(pattern, reduce_info_for_rfactor,
                                          RfactorType.SPLIT_LAST_REDUCE_AXIS_RFACTOR_FOR_ATOMIC)
            return

        progress.todo = RfactorAction(pattern, reduce_info_for_rfactor,
                                      RfactorType.SPLIT_LAST_REDUCE_AXIS_RFACTOR_FOR_NORMAL)
        return

    progress.todo = ReorderAction(pattern)
    return
