"""
common director
"""
from auto_search.solution_space.action import director_register
from auto_search.solution_space.action import SearchActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.search_action.remove_pad.remove_pad_action import RemovePadAction
from auto_search.solution_space.schedule_action.cache_read.cache_read_action import \
    CacheReadAction
from auto_search.compute_analysis import support_remove_pad_search


@director_register([ComputePattern.NORM], SearchActionType.ALIGN_PAD)
def direct_for_norm(progress):
    """
    norm direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    next_action = RemovePadAction(pattern)
    progress.todo = next_action


@director_register([ComputePattern.TUPLE_REDUCE, ComputePattern.REDUCE], SearchActionType.ALIGN_PAD)
def direct(progress):
    """
    reduce direct
    :param progress:
    :return:
    """
    pattern = progress.op_schedule_info.op_pattern
    if support_remove_pad_search(progress, progress.op_schedule_info.compute_graph_info):
        next_action = RemovePadAction(pattern)
        progress.todo = next_action
        return

    stages_info = progress.op_schedule_info.stages_info
    for stage_index, stage_info in enumerate(stages_info):
        if {'placeholder', 'workspace'} & set(stage_info.get('type', [])) \
                and 'src_cache_read' not in stage_info.get('type', []):
            next_action = CacheReadAction(pattern, stage_index)
            progress.todo = next_action
            return
