"""
common apply action
"""
from typing import NoReturn

from auto_search.solution_space.action import apply_action_register
from auto_search.solution_space.action import SearchActionType
from auto_search.compute_analysis import ComputePattern
from auto_search.solution_space.progress import Progress


@apply_action_register(
    [ComputePattern.ELEMENTWISE, ComputePattern.BROADCAST, ComputePattern.REDUCE, ComputePattern.NORM,
     ComputePattern.POOLING, ComputePattern.TUPLE_REDUCE],
    SearchActionType.DOUBLE_BUFFER)
def apply(next_progress: Progress, action_value: int) -> NoReturn:
    """
    double_buffer action value apply
    :param next_progress: Progress
    :param action_value: action value
    :return: no return
    """
    if action_value:
        next_progress.op_schedule_info.is_open_db = True
    else:
        next_progress.op_schedule_info.is_open_db = False
