"""
split action
"""
from auto_search.solution_space.action import VariableAction
from auto_search.solution_space.action import SearchActionType


class SplitAction(VariableAction):
    """
    SplitAction
    """
    def __init__(self, pattern, stage_index, sub_action_index, axis_index):
        super().__init__(pattern, SearchActionType.SPLIT)
        self.stage_index = stage_index
        self.sub_action_index = sub_action_index
        self.axis_index = axis_index
        self.cache_layer = 0
