import sys
from auto_tune.auto_tune_log import LOG_INSTANCE
from auto_tune.update_repository import tiling_merge_offline


def repo_merge_ga() -> bool:
    """
    GA repo merge entrance for msoptune
    """
    if len(sys.argv) < 3:
        LOG_INSTANCE.error("Src path and dst path are both required.")
        return False
    src_path_list = sys.argv[1].split(':')
    dst_path = sys.argv[2]
    ret = tiling_merge_offline(src_path_list, dst_path)
    return ret

if __name__ == "__main__":
    sys.exit(repo_merge_ga())
