from typing import Tuple, List, Dict
from llm_datadist import llm_wrapper


class LLMDataDistWrapper:
    @staticmethod
    def add_model(model_options: Dict[str, str]) -> Tuple[int, int]:
        return llm_wrapper.add_model(model_options)

    @staticmethod
    def run_model(model_id: int, device_id_to_inputs: Dict[int, List], req_desc: str) -> Tuple[int, List]:
        return llm_wrapper.run_model(model_id, device_id_to_inputs, req_desc)
