import sys
from auto_tune.auto_tune_log import LOG_INSTANCE
from tbe.common.repository_manager.utils.repository_merge_offline import repo_merge_offline


def repo_merge_unified() -> bool:
    """
    Unified_bank repo merge entrance for msoptune
    """
    if len(sys.argv) < 3:
        LOG_INSTANCE.error("Src path and dst path are both required.")
        return False
    src_path_list = sys.argv[1].split(':')
    dst_path = sys.argv[2]
    ret = repo_merge_offline(src_path_list, dst_path)
    return ret


if __name__ == "__main__":
    sys.exit(repo_merge_unified())
