from schedule_search import log


def proc(progress, action_mask, rules):
    """
    处理workspace的Mask
    """
    # 调用各个Rule
    log.dbg('progress: %s', progress.todo)
    for rule in rules:
        module = __import__(rule, fromlist=['1'])
        action_mask = module.proc(progress, action_mask)

    return action_mask
