def proc(progress, action_mask, rules):
    '''

    :param progress:
    :param action_mask:
    :param rules:
    :return:
    '''
    # 调用各个Rule
    for rule in rules:
        module = __import__(rule, fromlist=['1'])
        action_mask = module.proc(progress, action_mask)

    return action_mask
