from schedule_search import log
from schedule_search.controller.mcts_search.features import SEARCH_N


def proc(progress, action_mask):
    """
    规则内容：0表示切，1表示不切，其它全部mask掉
    """
    for idx in range(SEARCH_N):
        if idx > 1:
            action_mask[idx] = 0

    log.dbg('[%s]need_split_l0 action_mask: %s', progress.todo.stage_index,
            action_mask)
    return action_mask
