from schedule_search import log
from schedule_search import soc_cfg


def get_block_dim(axis_len):
    """

    :param axis_len:
    :return:
    """
    core_num = soc_cfg.get_core_num()

    # 只有一个stage需要bind时, block_dim小于core_num，轴长小于核数时，
    # block_dim直接取轴长
    if axis_len <= core_num:
        block_dim = axis_len
    else:
        block_dim = core_num
    log.dbg("block_dim: %s, axis_len: %s", block_dim, axis_len)
    return block_dim
