"""Custom datatype register"""

from tbe.tvm.target import datatype

# TVMTypeCode K_CUSTOM_BEGIN should be greater than 130 for custom datatype register
K_CUSTOM_BEGIN = 130


def data_type_register(type_name, type_code):
    """register new data"""
    if isinstance(type_name, str) and isinstance(type_code, int):
        if type_code >= K_CUSTOM_BEGIN :
            datatype.register(type_name, type_code)
        else:
            raise ValueError("Expect type_code greater than 129 for custom datatype register")
    else:
        raise ValueError("Expect type_name to be string or type_code to be int")
