"""
action
"""

from tbe.dsl.base.record import record


class Action:
    def __init__(self, func, args, kwargs, r_value):
        self._func = func
        self._args = args
        self._kwargs = kwargs
        self._r_value = r_value

    def execute(self):
        args, kwargs = self._get_params()
        tvm_tensor = self._func(*args, **kwargs)
        self._r_value.set_ori(tvm_tensor)

    def _get_params(self):
        args, kwargs = [], {}

        for x in self._args:
            if isinstance(x, record.Record):
                args.append(x.get_ori())
                continue
            args.append(x)

        for k, v in self._kwargs.items():
            if isinstance(v, record.Record):
                kwargs[k] = v.get_ori()
                continue
            kwargs[k] = v

        return args, kwargs
