"""
list record
"""

from tbe.dsl.base.record import record


class ListRecord(list, record.Record):
    def __init__(self, x):
        super().__init__(x)
        self._ori = x

    def __setitem__(self, index, item):
        super().__setitem__(index, item)
        self._ori[index] = item

    def get_ori(self):
        return self._ori

    def set_ori(self, x):
        self._ori = x
        self[:] = x

    def insert(self, index, item):
        super().insert(index, item)
        self._ori.insert(index, item)

    def append(self, item):
        super().append(item)
        self._ori.append(item)

    def extend(self, other):
        super().extend(other)
        self._ori.extend(other)
