"""
pool attr
"""

from dataclasses import dataclass
from typing import List

from tbe.dsl.unify_schedule import util


@dataclass
class PoolAttr:
    ori_window_axes: List[int] = None
    window_axes: List[int] = None
    kernel: List[int] = None
    strides: List[int] = None

    def __setattr__(self, key, value):
        if value is not None:
            self.__dict__[key] = util.shape_to_list(value)
            return
        super().__setattr__(key, value)

    @property
    def start_idx(self):
        return min(self.window_axes)

    @property
    def end_idx(self):
        return max(self.window_axes)

    @property
    def axes_len(self):
        return len(self.window_axes)
