"""
split info
"""

from dataclasses import dataclass
from typing import Union

from tbe import tvm
from tbe.dsl.boost_schedule_kit import stage_pro


@dataclass
class SplitInfo:
    stage: Union[stage_pro.StagePro, tvm.Stage] = None
    axis: tvm.IterVar = None
    factor: Union[int, tvm.Var] = None
    outer: tvm.IterVar = None
    inner: tvm.IterVar = None

    def do_split(self):
        self.outer, self.inner = self.stage.split(self.axis, factor=self.factor)
