"""
UB split ro, block split r
"""

from tbe.dsl.unify_schedule.constants import PoolGradPattern
from tbe.dsl.unify_schedule.vector.pool_grad import pool_grad_tilingcase
from tbe.dsl.unify_schedule.vector.pool_grad.tiling_cores.last import \
    last_tiling_core


class UbRkBlockR(last_tiling_core.LastTilingCore):
    def __init__(self, schedule):
        super().__init__(schedule)

        self._fn_outer = None

    def get_strategy():
        return (PoolGradPattern.LAST_WINDOW_AXIS, pool_grad_tilingcase.Strategy.UB_RK_BLOCK_R)

    def _do_split(self):
        self._fn_outer, inner = self._fn.split(self._fn_ah_axis, factor=16)
        self._fn.reorder(self._fn_outer, *self._fn_ak_axes, inner)

        self._ub_split.stage = self._dp
        self._ub_split.axis = self._rk_axes[self._ub_idx]
        self._ub_split.do_split()

        self._block_split.stage = self._dp
        self._block_split.axis = self._dp.fuse(*self._ro_axes[:])
        self._block_split.do_split()

        self._dp.reorder(self._block_split.outer,
                         self._block_split.inner,
                         *self._rk_axes[:self._ub_idx],
                         self._ub_split.outer,
                         self._ub_split.inner,
                         *self._rk_axes[self._ub_idx+1:],
                         *self._ak_axes,
                         self._at_axis)

    def _do_compute_at(self):
        for x in self._dp.ancestors:
            if x.is_placeholder() or x.is_scan_component() or x.is_compute_inline():
                continue
            x.compute_at(self._dp, self._ub_split.outer)
        self._dp.compute_at(self._fn, self._fn_outer)

        for x in self._dp.descendants:
            if x == self._fn:
                continue

            if x.op.name in ['col2img']:
                continue
            x.reverse_compute_at(self._dp, self._ub_split.outer)

    def _do_emit_insn(self):
        self._fn.emit_phony()
        self._dp.emit(self._ub_split.inner, attrs={"reduce_mode": "conditional_reduce",
                                                   "analysis_repeat_overlap": 1})
