"""
list util
"""

from itertools import chain


def append_only(xlist, item):
    if item not in xlist:
        xlist.append(item)


def extend_only(xlist, alist):
    for x in alist:
        append_only(xlist, x)


def uion(*args):
    return list(chain(*args))


def uion_only(*args):
    xlist = []
    for x in args:
        extend_only(xlist, x)
    return xlist


def sub(alist, blist):
    return [x for x in alist if x not in blist]


def unique(xlist):
    rlist = []
    for x in xlist:
        append_only(rlist, x)
    return rlist
