"""
elewise broadcast variable shape dispatch
"""
from tbe.common.utils.varshape.variable_shape import register_variable

from tbe.dsl.classifier.util import ClassifyInsMode
from . import elewise_variable_shape
from . import broadcast_variable_shape


@register_variable("elewise")
def variable_shape(inputs):
    """
    variable shape dispatch of elewise and broadcast
    :param inputs: inputs after classify
    """
    if len(inputs) == 0:
        return []
    classify_mode = inputs[0].get("classify_mode")
    if classify_mode == ClassifyInsMode.ELEWISE_CLASSIFY:
        return elewise_variable_shape.variable_shape(inputs)
    return broadcast_variable_shape.variable_shape(inputs)
