"""
sort variable shape
"""
from tbe.common.utils.varshape.variable_shape import register_variable
from tbe.dsl.base import operation


@register_variable("sort")
def variable_shape(inputs):
    # type: (list) -> list
    x_info = inputs[0]

    # new x var
    x_shape = []
    for index, value in enumerate(x_info["shape"]):
        if value == -1:
            _var = operation.var_inner("_x_dim_{}".format(index), x_info["range"][index], "int64")
            x_shape.append(_var)
        else:
            x_shape.append(value)

    # new k var
    if len(inputs) == 2:
        k_info = inputs[1]
        k_var = []
        for index, value in enumerate(k_info["shape"]):
            _var = operation.var_inner("_k_var_{}".format(index), k_info["range"][index], "int64")
            k_var.append(_var)

        return x_shape, k_var

    return x_shape
