from tbe.tik.common.common_util import is_scalar
from tbe.tik.common.common_util import is_scalar_array
from tbe.tik.common.common_util import is_tensor
from tbe.tik.common.common_util import is_expr
from tbe.tik.common.common_util import is_tensor_addr_list
from tbe.tik.common.common_util import is_vector_register
from tbe.tik.tik_lib.tik_source_info import source_info_decorator


@source_info_decorator()
def is_tik_var(obj):
    """
    check the argument is tik var
    Parameters
    ----------
    obj: checked variable

    Returns bool val
    -------

    """
    check_res = False
    check_func = (is_scalar, is_scalar_array, is_tensor, is_vector_register, is_tensor_addr_list, is_expr)

    for func_obj in check_func:
        if func_obj(obj):
            check_res = True
            break

    return check_res
